import React, { useState } from "react";

function App() {
  const [contact, setContact] = useState({
    firstName: "",
    lastName: "",
    emailAddress: ""
  });

  const handleChange = (e) => {
    const inputValue = e.target.value;
    const inputName = e.target.name;

    const {firstName: fName, lastName: lName, emailAddress: email} = contact;

    fName = 'Aras';
    lName = 'Sen';
    email = 'aras@gmail.com';

    setContact({
      firstName: 'Aras',
      lastName: lName,
      emailAddress: email
    });
  }

  const handleSubmit = () => {

  }



  return (
    <div className="container">
      <h1>
        Hello {contact.fName} {contact.lName}
      </h1>
      <p>{contact.email}</p>
      <form onSubmit={handleSubmit}>
        <input name="fName" placeholder="First Name" onChange={handleChange} />
        <input name="lName" placeholder="Last Name" onChange={handleChange} />
        <input name="email" placeholder="Email" onChange={handleChange} />
        <button type='submit'>Submit</button>
      </form>
    </div>
  );
}

export default App;
